import React, { useEffect, useState } from "react";
import { Bar, Doughnut } from "react-chartjs-2"; // Using simple charts
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from "chart.js";
import api from "../api";
import "./reportsPage.css";

// Register Chart Components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend, ArcElement);

function ReportsPage() {
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [period, setPeriod] = useState("month"); // 'today', 'week', 'month'

  // Safe defaults (prevents infinite loading)
  const [summary, setSummary] = useState({
    totalEarnings: 0,
    totalExpenses: 0,
    netProfit: 0,
    byDay: [],
    expensesByCategory: []
  });
  
  const [expensesList, setExpensesList] = useState([]);
  
  // Form State
  const [expenseForm, setExpenseForm] = useState({
    category: "",
    amount: "",
    notes: ""
  });
  const [isSaving, setIsSaving] = useState(false);

  // Helper: Get Today's Date
  const getToday = () => new Date().toISOString().slice(0, 10);

  /* --- 1. APPLY PRESET DATES --- */
  const applyPreset = (type) => {
    setPeriod(type);
    const now = new Date();
    let start, end;

    if (type === "today") {
      start = end = getToday();
    } else if (type === "week") {
      const day = now.getDay(); 
      const diff = now.getDate() - day + (day === 0 ? -6 : 1); // Monday
      const monday = new Date(now.setDate(diff));
      start = monday.toISOString().slice(0, 10);
      end = getToday();
    } else {
      // Month (Default)
      start = new Date(now.getFullYear(), now.getMonth(), 1).toISOString().slice(0, 10);
      end = getToday();
    }

    setFromDate(start);
    setToDate(end);
    fetchData(start, end);
  };

  /* --- 2. FETCH DATA --- */
  const fetchData = async (start, end) => {
    try {
      // Fetch Summary (Earnings/Expenses Stats)
      // Note: Assuming you have this endpoint. If not, stats will be 0.
      const summaryRes = await api.get(`/reports/summary?from=${start}&to=${end}`);
      setSummary(summaryRes.data);

      // Fetch Expense List
      const expRes = await api.get(`/expenses?from=${start}&to=${end}`);
      setExpensesList(expRes.data || []);
      
    } catch (err) {
      console.error("Error fetching report data", err);
      // Don't show alert loop, just log it. Page remains usable.
    }
  };

  // Initial Load
  useEffect(() => {
    applyPreset("month");
    // eslint-disable-next-line
  }, []);


  /* --- 3. SAVE EXPENSE --- */
  const handleSaveExpense = async (e) => {
    e.preventDefault();
    if (!expenseForm.category || !expenseForm.amount) {
      alert("Please fill Category and Amount");
      return;
    }

    try {
      setIsSaving(true);
      
      // Call your API
      await api.post("/expenses", {
        date: getToday(), // Default to today
        category: expenseForm.category,
        amount: Number(expenseForm.amount),
        notes: expenseForm.notes
      });

      // Clear Form
      setExpenseForm({ category: "", amount: "", notes: "" });

      // Refresh Data immediately to show updates
      fetchData(fromDate, toDate);

    } catch (err) {
      alert("Failed to save expense");
    } finally {
      setIsSaving(false);
    }
  };


  /* --- 4. SIMPLE CHART CONFIG --- */
  // Chart 1: Money In vs Out (Simple Bar)
  const barChartData = {
    labels: summary.byDay?.map(d => new Date(d.date).getDate()) || [], // Just show dates
    datasets: [
      {
        label: "Income (₹)",
        data: summary.byDay?.map(d => d.earnings) || [],
        backgroundColor: "#198754", // Green
        borderRadius: 4
      },
      {
        label: "Expense (₹)",
        data: summary.byDay?.map(d => d.expenses) || [],
        backgroundColor: "#dc3545", // Red
        borderRadius: 4
      }
    ]
  };

  // Chart 2: Where money went (Doughnut)
  const doughnutData = {
    labels: summary.expensesByCategory?.map(c => c.category) || [],
    datasets: [{
      data: summary.expensesByCategory?.map(c => c.total) || [],
      backgroundColor: ["#ff6384", "#36a2eb", "#ffce56", "#4bc0c0", "#9966ff"],
      borderWidth: 0
    }]
  };

  return (
    <div className="reports-container">
      
      {/* HEADER */}
      <div className="page-header">
        <div className="header-title">
          <h2>Finance Dashboard</h2>
          <p>Track your salon's profit & loss</p>
        </div>

        <div className="controls-wrapper">
          <button className={`preset-btn ${period==='today'?'active':''}`} onClick={() => applyPreset('today')}>Today</button>
          <button className={`preset-btn ${period==='week'?'active':''}`} onClick={() => applyPreset('week')}>Week</button>
          <button className={`preset-btn ${period==='month'?'active':''}`} onClick={() => applyPreset('month')}>Month</button>
          
          <div className="date-range">
            <input type="date" className="date-input" value={fromDate} onChange={e=>setFromDate(e.target.value)} />
            <span>-</span>
            <input type="date" className="date-input" value={toDate} onChange={e=>setToDate(e.target.value)} />
            <button className="go-btn" onClick={() => fetchData(fromDate, toDate)}>Go</button>
          </div>
        </div>
      </div>

      {/* BIG NUMBERS (KPIs) - Easiest for non-tech clients */}
      <div className="kpi-row">
        <div className="kpi-card green">
          <span className="kpi-title">Total Income</span>
          <div className="kpi-value">₹{summary.totalEarnings.toLocaleString()}</div>
        </div>
        <div className="kpi-card red">
          <span className="kpi-title">Total Expenses</span>
          <div className="kpi-value">₹{summary.totalExpenses.toLocaleString()}</div>
        </div>
        <div className="kpi-card blue">
          <span className="kpi-title">Net Profit</span>
          <div className="kpi-value" style={{color: summary.netProfit >= 0 ? '#333' : '#dc3545'}}>
            ₹{summary.netProfit.toLocaleString()}
          </div>
        </div>
      </div>

      <div className="content-grid">
        
        {/* LEFT PANEL: Visuals & History */}
        <div className="left-panel">
          
          {/* Main Chart */}
          <div className="chart-container">
            <h4 className="section-title">Income vs Expenses (Daily)</h4>
            <div style={{height: '250px'}}>
              <Bar 
                data={barChartData} 
                options={{ 
                  maintainAspectRatio: false, 
                  responsive: true,
                  plugins: { legend: { position: 'top' } }
                }} 
              />
            </div>
          </div>

          {/* Recent Expense List */}
          <div className="expense-list-card">
            <div className="list-header">Recent Expenses</div>
            <table className="simple-table">
              <tbody>
                {expensesList.map(e => (
                  <tr key={e._id}>
                    <td style={{width:'20%'}}>{new Date(e.date).toLocaleDateString()}</td>
                    <td style={{width:'30%'}}><strong>{e.category}</strong></td>
                    <td style={{color:'#888'}}>{e.notes || "-"}</td>
                    <td className="amount-red">₹{e.amount}</td>
                  </tr>
                ))}
                {expensesList.length === 0 && (
                  <tr><td colSpan="4" style={{textAlign:'center', padding:20, color:'#999'}}>No expenses found</td></tr>
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* RIGHT PANEL: Add Expense Form */}
        <div className="right-panel">
          <h4 className="section-title">➕ Add New Expense</h4>
          
          <form onSubmit={handleSaveExpense}>
            <div className="form-group">
              <label className="form-label">Category</label>
              <select 
                className="modern-select" 
                value={expenseForm.category}
                onChange={e => setExpenseForm({...expenseForm, category: e.target.value})}
              >
                <option value="">Select...</option>
                <option value="Rent">Rent</option>
                <option value="Electricity">Electricity</option>
                <option value="Inventory">Products/Inventory</option>
                <option value="Salary">Staff Salary</option>
                <option value="Marketing">Marketing</option>
                <option value="Maintenance">Maintenance</option>
                <option value="Tea/Snacks">Tea/Snacks</option>
                <option value="Other">Other</option>
              </select>
            </div>

            <div className="form-group">
              <label className="form-label">Amount (₹)</label>
              <input 
                type="number" 
                className="modern-input" 
                placeholder="0"
                value={expenseForm.amount}
                onChange={e => setExpenseForm({...expenseForm, amount: e.target.value})}
              />
            </div>

            <div className="form-group">
              <label className="form-label">Notes</label>
              <input 
                type="text" 
                className="modern-input" 
                placeholder="Details..."
                value={expenseForm.notes}
                onChange={e => setExpenseForm({...expenseForm, notes: e.target.value})}
              />
            </div>

            {/* BIG SAVE BUTTON */}
            <button type="submit" className="save-btn" disabled={isSaving}>
              {isSaving ? "Saving..." : "Save Expense"}
            </button>
          </form>

          {/* Simple Pie Chart for Expense Breakdown */}
          <div className="doughnut-wrapper">
             <div className="doughnut-title">Expense Breakdown</div>
             
             {/* Adjusted height to 160px so it fits better */}
             <div style={{height: 160, width: '100%', display:'flex', justifyContent:'center'}}>
                {summary.totalExpenses > 0 ? (
                   <Doughnut 
                     data={doughnutData} 
                     options={{ 
                       maintainAspectRatio: false,
                       plugins: {
                         legend: {
                           position: 'bottom', // Puts legend below chart
                           labels: { boxWidth: 10, font: { size: 10 } } // Makes text smaller
                         }
                       }
                     }} 
                   />
                ) : (
                   <div style={{color:'#ccc', alignSelf:'center', fontSize: '0.9rem'}}>
                      No Data Yet
                   </div>
                )}
             </div>
          </div>

        </div> {/* End of right-panel */}

      </div> {/* End of content-grid */}
    </div>
  );
}

export default ReportsPage;
