import React from "react";
import { Link } from "react-router-dom";


const Navbar = ({ user, onLogout }) => {
  return (
    <nav className="navbar navbar-expand-lg fixed-top custom-navbar">
      <div className="container-fluid px-4 position-relative">
        
        {/* LEFT SIDE: Navigation Links */}
        <div className="d-flex align-items-center">
          <ul className="navbar-nav flex-row gap-4">
            <li className="nav-item">
              <Link className="nav-link custom-link" to="/">Clients</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link custom-link" to="/services">Services</Link>
            </li>
            <li className="nav-item">
              <Link className="nav-link custom-link" to="/reports">Reports</Link>
            </li>
          </ul>
        </div>

        {/* CENTER: Brand Name (Absolutely Positioned) */}
        <div className="position-absolute start-50 translate-middle-x">
          <Link className="navbar-brand brand-text" to="/">
            SALON<span className="brand-highlight">CRM</span>
          </Link>
        </div>

        {/* RIGHT SIDE: User / Login */}
        <div className="d-flex align-items-center ms-auto">
          {user ? (
            <div className="d-flex align-items-center gap-3">
              <span className="user-greeting d-none d-md-block">
                Hi, <strong>{user.name}</strong>
              </span>
              <button 
                className="btn btn-sm btn-logout" 
                onClick={onLogout}
              >
                Logout
              </button>
            </div>
          ) : (
            <Link className="btn btn-sm btn-login" to="/login">
              Login
            </Link>
          )}
        </div>

      </div>
    </nav>
  );
};

export default Navbar;
