import React, { useCallback, useEffect, useState, useRef } from "react"; // 1. Added useRef
import { Link } from "react-router-dom";
import api from "../api";
import "./clientsPage.css";

function ClientsPage() {
  const [clients, setClients] = useState([]);
  const [allClients, setAllClients] = useState([]);
  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  // Add client
  const [showForm, setShowForm] = useState(false);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [notes, setNotes] = useState("");
  const [error, setError] = useState("");
  const [saving, setSaving] = useState(false);

  // 2. Create the reference
  const formSectionRef = useRef(null);

  /* ================= TOGGLE & SCROLL LOGIC ================= */
  const handleAddNewClick = () => {
    if (!showForm) {
      setShowForm(true);
      // We use a small timeout to ensure the DOM element exists before scrolling
      setTimeout(() => {
        formSectionRef.current?.scrollIntoView({ 
          behavior: "smooth", 
          block: "start" 
        });
      }, 100);
    } else {
      setShowForm(false);
    }
  };

  /* ================= FETCH CLIENTS ================= */
  const fetchClients = useCallback(async () => {
    try {
      setLoading(true);
      const res = await api.get("/clients");
      setClients(res.data || []);
      setAllClients(res.data || []);
    } catch (err) {
      console.error(err);
      setClients([]);
      setAllClients([]);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchClients();
  }, [fetchClients]);

  /* ================= LOCAL FAST SEARCH ================= */
  const handleSearch = (e) => {
    const value = e.target.value;
    setSearch(value);

    if (!value.trim()) {
      setClients(allClients);
      return;
    }

    const query = value.toLowerCase();
    const filtered = allClients.filter(
      (c) => c.name.toLowerCase().includes(query) || c.phone.includes(query)
    );
    setClients(filtered);
  };

  /* ================= ADD CLIENT ================= */
  const addClient = async (e) => {
    e.preventDefault();
    setError("");

    if (!name || !phone) {
      setError("Name & phone required");
      return;
    }

    try {
      setSaving(true);
      const res = await api.post("/clients", { name, phone, notes });

      setClients((prev) => [res.data, ...prev]);
      setAllClients((prev) => [res.data, ...prev]);

      setName("");
      setPhone("");
      setNotes("");
      setShowForm(false);
    } catch (err) {
      console.error(err);
      setError("Failed to add client");
    } finally {
      setSaving(false);
    }
  };

  /* ================= DELETE CLIENT ================= */
  const deleteClient = async (id) => {
    if (!window.confirm("Delete this client permanently?")) return;
    try {
      await api.delete(`/clients/${id}`);
      setClients((prev) => prev.filter((c) => c._id !== id));
      setAllClients((prev) => prev.filter((c) => c._id !== id));
    } catch (err) {
      console.error(err);
      alert("Failed to delete client");
    }
  };

  const initials = (name) =>
    name?.split(" ").map((n) => n[0]).join("").toUpperCase();

  return (
    <div className="container-fluid px-4">
      {/* HEADER */}
      <div className="clients-top row align-items-center">
        <div className="col-12 col-md">
          <h3 className="page-title">Clients</h3>
          <p className="page-subtitle">Manage and access your salon‚Äôs clientele</p>
        </div>

        <div className="col-12 col-md-auto mt-2 mt-md-0 text-md-end">
          <button
            className="add-client-btn mobile-full"
            onClick={handleAddNewClick} // 3. Trigger the scroll logic
          >
            {showForm ? "Cancel" : "+ New Client"}
          </button>
        </div>
      </div>

      {/* ADD CLIENT FORM - Placed above search as requested */}
      {showForm && (
        <div className="lux-card mt-3" ref={formSectionRef}> {/* 4. Attach Ref here */}
          <h5 className="mb-3" style={{ fontWeight: 700 }}>Add New Client</h5>
          {error && <div className="alert alert-danger">{error}</div>}
          <form onSubmit={addClient} className="row g-2">
            <div className="col-md-4">
              <input
                className="form-control"
                placeholder="Client name"
                value={name}
                autoFocus // Bonus: Automatically focus the first input
                onChange={(e) => setName(e.target.value)}
              />
            </div>
            <div className="col-md-4">
              <input
                className="form-control"
                placeholder="Phone number"
                value={phone}
                inputMode="numeric"
                onChange={(e) => setPhone(e.target.value.replace(/\D/g, ""))}
              />
            </div>
            <div className="col-md-4">
              <input
                className="form-control"
                placeholder="Notes (optional)"
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
              />
            </div>
            <div className="col-12 text-end">
              <button className="save-client-btn" disabled={saving}>
                {saving ? "Saving..." : "Save Client"}
              </button>
            </div>
          </form>
        </div>
      )}

      {/* SEARCH */}
      <input
        className="client-search"
        placeholder="Search by name or phone number‚Ä¶"
        value={search}
        onChange={handleSearch}
      />

      {/* CLIENT GRID */}
      <div className="clients-grid mt-4">
        {loading ? (
          <p className="text-muted">Loading‚Ä¶</p>
        ) : clients.length === 0 ? (
          <p className="text-muted">No clients found</p>
        ) : (
          clients.map((c) => (
            <Link key={c._id} to={`/clients/${c._id}`} className="client-card-link">
              <div className="client-card lux-card">
                <button
                  className="delete-client-btn"
                  onClick={(e) => {
                    e.preventDefault();
                    e.stopPropagation();
                    deleteClient(c._id);
                  }}
                >
                  üóëÔ∏è
                </button>
                <div className="client-row">
                  <div className="client-avatar">{initials(c.name)}</div>
                  <div>
                    <div className="client-name">{c.name}</div>
                    <div className="client-phone">{c.phone}</div>
                  </div>
                </div>
                <span className="open-client">View Profile ‚Üí</span>
              </div>
            </Link>
          ))
        )}
      </div>
    </div>
  );
}

export default ClientsPage;
